import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';

import { useFonts } from '@use-expo/font';
import * as Font from "expo-font";
import { Ionicons, FontAwesome } from "@expo/vector-icons";
import { NavigationContainer } from "@react-navigation/native";
import { Asset } from "expo-asset";

import { AppLoading } from 'expo';

require("@expo/vector-icons/build/vendor/react-native-vector-icons/Fonts/Ionicons.ttf");

const cacheImages = images =>
  images.map(image => {
    if (typeof image === 'string') {
      return Image.prefetch(image);
    } else {
      return Asset.fromModule(image).downloadAsync();
    }
});

const cacheFonts = fonts => fonts.map(font => Font.loadAsync(font));

const bmhannaFonts = {
  'BMHANNA': require('./assets/fonts/BMHANNAPro.ttf'),
};

export default function App() {
  const [isReady, setIsReady] = useState(false);

  const loadAssets = () => {
    console.log('되나?')
    const fonts = cacheFonts([Ionicons.font, FontAwesome.font]);
    return Promise.all([...images, ...fonts]);
  }

  const onFinish = () => setIsReady(true);

  const [isLoaded] = useFonts(bmhannaFonts);

  if (!isLoaded) {
    return <AppLoading />;
  }

  return isReady ? (
    <View style={styles.container}>
      <Image source={require('./assets/logo.gif')} style={styles.logo} />
      <Text style={styles.text}>PACKMAN</Text>
      <StatusBar style="auto" />
    </View>
  ) : (
    <AppLoading 
      startAsync={loadAssets}
      onFinish={onFinish}
      onError={console.error}
    />
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 200,
    resizeMode: 'contain'
  },
  text: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#174793',
    fontFamily: 'BMHANNA'
  }
});
